mapper.insert:
INSERT INTO NOTICE(
	   NOTICE_CODE
	 , TITLE
	 , ID
	 , NAME
	 , CONTENT
	 , CREATE_DATE
)VALUES(
	   (SELECT 'NOTICE_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(NOTICE_CODE, 8, 3))), 0) + 1, 3, '0') FROM NOTICE)	
	 , #{title}
	 , #{id}
	 , #{name}
	 , #{content}
	 , #{createDate}
)

	INSERT INTO QNA(
            QNA_CODE
          , TITLE
          , ID
          , NAME
          , CONTENT
          , CREATE_DATE
          , QNA_PW
	)VALUES(
			(SELECT 'QNA_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(QNA_CODE, 5, 3))), 0) + 1, 3, '0') FROM QNA)
			,#{title}
			,#{id}
			,#{name}
			,#{content}
			,#{createDate}
			,#{qnaPw}
)

INSERT INTO QNA_ANSWER(
            ANSWER_CODE
          , CONTENT
          , ID
          , NAME
          , CREATE_DATE
          , QNA_CODE
)VALUES(
        (SELECT 'ANSWER_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ANSWER_CODE, 8, 3))), 0) + 1, 3, '0') FROM QNA_ANSWER)
        ,#{content}
        ,#{id}
        ,#{name}
        ,#{createDate}
        ,#{qnaCode}
)
