mapper.insert:
INSERT INTO MEMBER (
	ID
	, PW
	, NAME
	, TELL
	, BIRTH
	, ADDR
	
	, GENDER
	
	
	, FAVORITE
	
) VALUES (
	#{id}
	, #{pw}
	, #{name}
	, #{tell}
	, #{birth}
	, #{addr}
	
	, #{gender}
	
	
	, #{favorite}
	
)

mapper.select:
SELECT ID
FROM MEMBER
WHERE ID = #{id}

SELECT ROW_NUM , RENTAL_CNT , RESERVE_CNT , ID , NAME , BIRTH FROM ( SELECT ROWNUM AS ROW_NUM , RENTAL_CNT , RESERVE_CNT , ID , NAME , BIRTH FROM ( SELECT ROWNUM AS ROW_NUM , (SELECT COUNT(BOOK_CODE) FROM RENTAL WHERE RETURN_DATE IS NULL AND ID = MEMBER.ID) AS RENTAL_CNT , (SELECT COUNT(BOOK_CODE) FROM RESERVE WHERE ID = MEMBER.ID) AS RESERVE_CNT , ID , NAME , TO_CHAR(BIRTH,'YYYY-MM-DD') AS BIRTH FROM MEMBER WHERE IS_DELETE = 'N' AND IS_ADMIN = 'N'
AND UPPER(ID) LIKE UPPER('%'||#{searchValue}||'%')
ORDER BY ID ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}
